import { Brain, Upload, AlertTriangle, CheckCircle2, FileImage } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function AIDeepfake() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [preview, setPreview] = useState<string | null>(null);
  const [analyzing, setAnalyzing] = useState(false);
  const [results, setResults] = useState<any>(null);
  const { toast } = useToast();

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        setPreview(event.target?.result as string);
      };
      reader.readAsDataURL(file);
      setResults(null);
    }
  };

  const simulateAnalysis = () => {
    setAnalyzing(true);
    setTimeout(() => {
      const isDeepfake = Math.random() > 0.7;
      setResults({
        isDeepfake,
        confidence: isDeepfake ? 87 : 94,
        aiModel: "DeepFake Detection v3.2",
        indicators: isDeepfake ? [
          { name: "Facial Inconsistency", score: 78 },
          { name: "Lighting Artifacts", score: 82 },
          { name: "Edge Anomalies", score: 91 },
          { name: "Texture Analysis", score: 85 },
        ] : [
          { name: "Natural Features", score: 95 },
          { name: "Consistent Lighting", score: 93 },
          { name: "Authentic Edges", score: 96 },
          { name: "Genuine Texture", score: 92 },
        ],
      });
      setAnalyzing(false);
      toast({
        title: "Analysis Complete",
        description: isDeepfake ? "Potential deepfake detected" : "Image appears authentic",
      });
    }, 2500);
  };

  return (
    <div className="space-y-6 p-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-semibold">AI / Deepfake Detection</h1>
        <p className="text-muted-foreground">
          Advanced AI-powered detection of deepfake images and manipulated media
        </p>
      </div>

      <div className="grid gap-6 lg:grid-cols-2">
        {/* Upload Section */}
        <Card className="p-6">
          <h3 className="mb-4 text-lg font-semibold flex items-center gap-2">
            <Brain className="h-5 w-5 text-primary" />
            Upload Media for Analysis
          </h3>

          <div className="space-y-4">
            <div className="flex items-center justify-center">
              {preview ? (
                <div className="relative w-full">
                  <img
                    src={preview}
                    alt="Preview"
                    className="max-h-96 w-full rounded-lg object-contain border border-border"
                  />
                </div>
              ) : (
                <label className="flex min-h-48 w-full cursor-pointer flex-col items-center justify-center rounded-lg border-2 border-dashed border-border hover-elevate active-elevate-2">
                  <FileImage className="mb-3 h-12 w-12 text-muted-foreground" />
                  <p className="text-sm text-muted-foreground">
                    Click to upload image or video
                  </p>
                  <p className="text-xs text-muted-foreground">
                    Supported: PNG, JPG, MP4, AVI up to 50MB
                  </p>
                  <Input
                    type="file"
                    className="hidden"
                    accept="image/*,video/*"
                    onChange={handleFileChange}
                    data-testid="input-deepfake-upload"
                  />
                </label>
              )}
            </div>

            {selectedFile && (
              <div className="space-y-2">
                <p className="text-sm font-medium">Selected File:</p>
                <div className="rounded-lg bg-muted p-3">
                  <p className="text-sm font-mono">{selectedFile.name}</p>
                  <p className="text-xs text-muted-foreground">
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
              </div>
            )}

            <div className="flex gap-2">
              {selectedFile && !results && (
                <Button
                  onClick={simulateAnalysis}
                  disabled={analyzing}
                  className="flex-1"
                  data-testid="button-analyze-deepfake"
                >
                  {analyzing ? (
                    <>
                      <div className="mr-2 h-4 w-4 animate-spin rounded-full border-2 border-primary-foreground border-t-transparent" />
                      Analyzing with AI...
                    </>
                  ) : (
                    <>
                      <Upload className="mr-1 h-4 w-4" />
                      Analyze for Deepfake
                    </>
                  )}
                </Button>
              )}
              {selectedFile && (
                <Button
                  variant="outline"
                  onClick={() => {
                    setSelectedFile(null);
                    setPreview(null);
                    setResults(null);
                  }}
                  data-testid="button-clear-deepfake"
                >
                  Clear
                </Button>
              )}
            </div>
          </div>
        </Card>

        {/* Results Section */}
        <Card className="p-6">
          <h3 className="mb-4 text-lg font-semibold">Detection Results</h3>

          {!results ? (
            <div className="flex min-h-48 flex-col items-center justify-center rounded-lg border-2 border-dashed border-border">
              <Brain className="mb-3 h-12 w-12 text-muted-foreground" />
              <p className="text-sm text-muted-foreground">
                Upload media to begin AI analysis
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Detection Status */}
              <div className="rounded-lg border border-border p-6 text-center">
                {results.isDeepfake ? (
                  <>
                    <div className="mx-auto mb-3 flex h-16 w-16 items-center justify-center rounded-full bg-destructive/10">
                      <AlertTriangle className="h-8 w-8 text-destructive" />
                    </div>
                    <Badge variant="destructive" className="mb-2">
                      Deepfake Detected
                    </Badge>
                  </>
                ) : (
                  <>
                    <div className="mx-auto mb-3 flex h-16 w-16 items-center justify-center rounded-full bg-chart-4/10">
                      <CheckCircle2 className="h-8 w-8 text-chart-4" />
                    </div>
                    <Badge className="mb-2 bg-chart-4/10 text-chart-4">
                      Appears Authentic
                    </Badge>
                  </>
                )}
                <p className="text-2xl font-bold" data-testid="text-confidence">
                  {results.confidence}% Confidence
                </p>
                <p className="text-sm text-muted-foreground">
                  {results.aiModel}
                </p>
              </div>

              {/* Indicators */}
              <div className="space-y-2">
                <p className="text-sm font-medium">Analysis Indicators</p>
                {results.indicators.map((indicator: any, index: number) => (
                  <div key={index} className="space-y-1">
                    <div className="flex items-center justify-between">
                      <span className="text-sm">{indicator.name}</span>
                      <span className="text-sm text-muted-foreground">
                        {indicator.score}%
                      </span>
                    </div>
                    <div className="h-2 w-full overflow-hidden rounded-full bg-muted">
                      <div
                        className={`h-full ${
                          results.isDeepfake
                            ? indicator.score > 85
                              ? "bg-destructive"
                              : "bg-chart-5"
                            : "bg-chart-4"
                        }`}
                        style={{ width: `${indicator.score}%` }}
                      />
                    </div>
                  </div>
                ))}
              </div>

              <div className="rounded-lg bg-muted p-3">
                <p className="text-xs text-muted-foreground">
                  {results.isDeepfake
                    ? "âš ï¸ This media shows signs of AI manipulation. Further investigation recommended."
                    : "âœ“ No significant manipulation detected. Media appears to be authentic."}
                </p>
              </div>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
}

